import { useState } from "react";
import Form from "./components/Form";
import Team from "./components/Team"
import Header from "./components/Header"
import CardCopy from "./components/CardCopy"

function App() {

  const listGender = [
    {
      name:'RPG',
      firstColor:'#f4b4a1',
      secondColor:'#461542',
    },
    {
      name:'FPS',
      firstColor:'#ffe042',
      secondColor:'#5b1f58',
    },
    {
      name:'MOBA',
      firstColor:'#0e387a',
      secondColor:'#6f2a6d',
    }
  ]

  const [games,setGames] = useState([])

  const aNewGameAdd = (game) =>{
    console.log(game)
    setGames([...games, game])
  }

  return (
    <div className="App">
      <header className="App-header">
        <Header/>
        <CardCopy firstColor='#'/>
        <Form listGender ={listGender.map(gender => gender.name)}  aRegisteredGame={game => aNewGameAdd(game)}/>
        {listGender.map(gender=> <Team 
        key={gender.name} 
        name={gender.name} 
        firstColor={gender.firstColor} 
        secondColor={gender.secondColor}
        games={games.filter(game => game.gender === gender.name)}
        />)}
      </header>
    </div>
  );
}

export default App;
