import { useState } from "react";
import Form from "./components/Form";
import Team from "./components/Team"
import List from "./components/List"

function App() {

  const listGender = [
    {
      name:'RPG',
      firstColor:'#A6D157',
      secondColor:'#F0F8E2',
    },
    {
      name:'FPS',
      firstColor:'#E06B69',
      secondColor:'#FDE7E8',
    },
    {
      name:'MOBA',
      firstColor:'D86EBF',
      secondColor:'#FAE5F5',
    }
  ]

//colaborador = game , colaboradores = games, aNewGameAdd = aoNovoColaboradorAdicionado, aRegisteredGame = aoColaboradorCadastrado
//listGender = times, 
  const [games,setGames] = useState([])

  const aNewGameAdd = (game) =>{
    console.log(game)
    setGames({...games, game})
  }

  return (
    <div className="App">
      <header className="App-header">
        <Form aRegisteredGame={game => aNewGameAdd(game)}/>
        <Team name="Test"/>
      </header>
    </div>
  );
}

export default App;
