import React, { useEffect } from 'react';
import './Header.css';

const Header = () => {
  useEffect(() => {
    // Implementação do efeito de rolagem
    const $curve = document.getElementById('curve');
    const defaultCurveValue = 350;
    const curveRate = 3;
    let last_known_scroll_position = 0;
    let ticking = false;

    const scrollEvent = (scrollPos) => {
      if (scrollPos >= 0 && scrollPos < defaultCurveValue) {
        const curveValue = defaultCurveValue - parseFloat(scrollPos / curveRate);
        $curve.setAttribute(
          'd',
          `M 800 300 Q 400 ${curveValue} 0 300 L 0 0 L 800 0 L 800 300 Z`
        );
      }
    };

    const handleScroll = () => {
      last_known_scroll_position = window.scrollY;

      if (!ticking) {
        window.requestAnimationFrame(() => {
          scrollEvent(last_known_scroll_position);
          ticking = false;
        });
      }

      ticking = true;
    };

    window.addEventListener('scroll', handleScroll);

    // Cleanup para evitar vazamento de memória
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []); // A função useEffect será executada apenas uma vez, ao montar o componente

  return (
    <div className='a'>
      <div className="svg-container">
        <svg viewBox="0 0 800 400" className="svg">
          <path id="curve" fill="#50c6d8" d="M 800 300 Q 400 350 0 300 L 0 0 L 800 0 L 800 300 Z">
          </path>
        </svg>
      </div>
      <div>
        <header>
          <h1>This is a header title</h1>
          <h3>Here you are, scroll down.</h3>
        </header>
      </div>
    </div>
  );
}

export default Header;
